package io.netty.buffer.manager;import io.netty.buffer.UnpooledByteBufAllocator;/** * @author likai * @date 2024/3/7 15:19 * @email likai9376@163.com * @desc */public class ByteBufAllocatorMetircMBeanService implements ByteBufAllocatorMetricMBean{    UnpooledByteBufAllocator unpooledByteBufAllocator = UnpooledByteBufAllocator.DEFAULT;    @Override    public long getDirectMemory() {        return unpooledByteBufAllocator.metric().usedDirectMemory();    }    @Override    public long getHeapMemory() {        return unpooledByteBufAllocator.metric().usedHeapMemory();    }}